import React, { useState, useRef, useEffect } from 'react';
import Select from 'react-select'
import './index.css';
import greendb from './greendb.json';
import wordlist from './wordlist.json';
import classes from './Site.module.css';
import Badge from '@material-ui/core/Badge';



const App = props => {

  const customStyles = {
    control: (base, state) => ({
      ...base,
      background: "rgba(45,45,45,0.3)",
      // match with the menu
      borderRadius: state.isFocused ? "3px 3px 0 0" : 3,
      // Overwrittes the different states of border
      borderColor: state.isFocused ? "rgba(255,255,255,0.5)" : "rgba(255,255,255,0.9)",
      // Removes weird border around container      
      // "&:hover": {
      //   // Overwrittes the different states of border
      //   borderColor: state.isFocused ? "red" : "blue"

      // }
    }),
    menu: base => ({
      ...base,
      // override border radius to match the box
      borderRadius: 0,
      // kill the gap
      marginTop: 0
    }),
    menuList: base => ({
      ...base,
      // kill the white space on first and last option
      padding: 0
    }),
    singleValue: base => ({
      ...base,
      color:'white'            
    })


  }

  useEffect(() => { 
    
  },[])


  const handleClick = (e) => {
    console.log(e)
    setSelectedOption({value:e, label:e})
  }
 
  const top5buttons = () => {
    return (
      <div>
      <button value={''} onClick={(e) => handleClick(e.target.value)}>{"back to default"}</button>
      <Badge badgeContent={freqSorted[0]} color="primary"><button class={classes.keybutton} value={options[0].value} onClick={(e) => handleClick(e.target.value)}>{options[0].value}</button></Badge>
      <Badge badgeContent={freqSorted[1]} color="primary"><button class={classes.keybutton} value={options[1].value} onClick={(e) => handleClick(e.target.value)}>{options[1].value}</button></Badge>
      <Badge badgeContent={freqSorted[2]} color="primary"><button class={classes.keybutton} value={options[2].value} onClick={(e) => handleClick(e.target.value)}>{options[2].value}</button></Badge>
      <Badge badgeContent={freqSorted[3]} color="primary"><button class={classes.keybutton} value={options[3].value} onClick={(e) => handleClick(e.target.value)}>{options[3].value}</button></Badge>
      <Badge badgeContent={freqSorted[4]} color="primary"><button class={classes.keybutton} value={options[4].value} onClick={(e) => handleClick(e.target.value)}>{options[4].value}</button></Badge>
      </div>
    )
  
  }    
  
  const dataPresent = (input) => {
    return (
      <div>
      {result.map(e=> {return <a href={e.Link}><button className={classes.buttonActive}>{e.Name}</button><br/></a>})}
      </div>
    )
  
  }    

