import React, { useState, useRef, useEffect } from 'react';
import Select from 'react-select'
import './index.css';
import greendb from './greendb.json';
import wordlist from './wordlist.json';
import classes from './Site.module.css';
import { Badge, Chip } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import StackGrid from "react-stack-grid";
import { Web, Videocam, Description, PictureAsPdf} from '@material-ui/icons';
import ReactGA from 'react-ga';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';


const App = props => {

  const data = [
    {
      name: 'Page A',
      uv: 4000,
      pv: 2400,
      amt: 2400,
    },
    {
      name: 'Page B',
      uv: 3000,
      pv: 1398,
      amt: 2210,
    },
    {
      name: 'Page C',
      uv: 2000,
      pv: 9800,
      amt: 2290,
    },
    {
      name: 'Page D',
      uv: 2780,
      pv: 3908,
      amt: 2000,
    },
    {
      name: 'Page E',
      uv: 1890,
      pv: 4800,
      amt: 2181,
    },
    {
      name: 'Page F',
      uv: 2390,
      pv: 3800,
      amt: 2500,
    },
    {
      name: 'Page G',
      uv: 3490,
      pv: 4300,
      amt: 2100,
    },
  ];

const renderLineChart = (
  <BarChart
  width={500}
  height={300}
  data={data}
  margin={{
    top: 5,
    right: 30,
    left: 20,
    bottom: 5,
  }}
>
  <CartesianGrid strokeDasharray="3 3" />
  <XAxis dataKey="name" />
  <YAxis />
  <Tooltip />
  <Legend />
  <Bar dataKey="pv" fill="#8884d8" />
  <Bar dataKey="uv" fill="#82ca9d" />
</BarChart>
);

  
return (
<div class={classes.main}>
  <div class={classes.header}>  
    <div class={classes.title}>{"Data explorer Africa's urban Economy 2022"}</div>    
    <div class={classes.box}>
    <Select 
    // styles={customStyles}
    // options={options} 
    // // isMulti 
    // value={selectedOption}
    // onChange={setSelectedOption}
    /></div>    
  </div>
  {renderLineChart}
</div>
  );
};

export default App;
