import React, { useState, useRef, useEffect } from 'react';
import Select from 'react-select'
import './index.css';
import greendb from './greendb.json';
import wordlist from './wordlist.json';
import classes from './Site.module.css';
import { Badge, Chip } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import StackGrid from "react-stack-grid";
import { Web, Videocam, Description, PictureAsPdf} from '@material-ui/icons';
import ReactGA from 'react-ga';
import { LineChart, Line, CartesianGrid, XAxis, YAxis  } from 'recharts';

const App = props => {

  const data = [{name: 'Page A', uv: 400, pv: 2400, amt: 2400}]

const renderLineChart = (
  <LineChart width={600} height={300} data={data} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
  <Line type="monotone" dataKey="uv" stroke="#ffffff" />
  <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
  <XAxis dataKey="name" />
  <YAxis />
</LineChart>
);

  
return (
<div class={classes.main}>
  <div class={classes.header}>  
    <div class={classes.title}>{"Data explorer Africa's urban Economy 2022"}</div>    
    <div class={classes.box}>
    <Select 
    // styles={customStyles}
    // options={options} 
    // // isMulti 
    // value={selectedOption}
    // onChange={setSelectedOption}
    /></div>    
  </div>
  {renderLineChart}
</div>
  );
};

export default App;
