import React from 'react';
import ReactDOM from 'react-dom';
import mapboxgl from 'mapbox-gl';
import axios from 'axios';
import Select from 'react-select';
import sci from './sci.json';
import countryData from './country.json';
import classes from './Site.module.css';
import { BarChart, Cell, Bar, Tooltip, XAxis, YAxis } from 'recharts';

//iso_3166_1_alpha_3
///6808: 520  baseline
//// divide 20% percentile per country//////

//// Access token for Africapolis mapbox account
mapboxgl.accessToken = 'pk.eyJ1IjoibWttZCIsImEiOiJjajBqYjJpY2owMDE0Mndsbml0d2V1ZXczIn0.el8wQmA-TSJp2ggX8fJ1rA';

const colors = ["#F3F2E8","#D4EBE2","#B6E3DB","#97DCD5","#79D5CF","#5ACEC9","#3CC6C2","#1DBFBC","#333333"]



//////////////////

// sort array ascending
console.log(countryData["AF"])
let top5_list=[[],[],[],[],[]];
let result=[];

const asc = arr => arr.sort((a, b) => a - b);

const sum = arr => arr.reduce((a, b) => a + b, 0);

const mean = arr => sum(arr) / arr.length;

const quantile = (arr, q) => {
    const sorted = asc(arr);
    const pos = (sorted.length - 1) * q;
    const base = Math.floor(pos);
    const rest = pos - base;
    if (sorted[base + 1] !== undefined) {
        return sorted[base] + rest * (sorted[base + 1] - sorted[base]);
    } else {
        return sorted[base];
    }
};

const legend = (
<div>
  <h4>Social Connected Index</h4>
<div><span style={{backgroundColor: colors[0]}}></span>{"< 1x (20th percentile selected country)"}</div>
<div><span style={{backgroundColor: colors[1]}}></span>1-2x</div>
<div><span style={{backgroundColor: colors[2]}}></span>2-3x</div>
<div><span style={{backgroundColor: colors[3]}}></span>3-5x</div>
<div><span style={{backgroundColor: colors[4]}}></span>5-10x</div>
<div><span style={{backgroundColor: colors[5]}}></span>10-25x</div>
<div><span style={{backgroundColor: colors[6]}}></span>25-100x</div>
<div><span style={{backgroundColor: colors[7]}}></span>{">= 100x"}</div>
<div><span style={{backgroundColor: colors[8]}}></span>{"Data not available"}</div>
</div>
);


const agglomeration_fill_color =[
  "step",
  ["get", "Population_2015"],
  "hsla(0, 0%, 8%, 0.5)",
  10000,
  "hsla(55, 30%, 93%, 0.5)",
  30000,
  "hsla(60, 100%, 90%, 0.5)",
  100000,
  "hsla(98, 55%, 81%, 0.5)",
  300000,
  "hsla(166, 44%, 65%, 0.5)",
  1000000,
  "hsla(186, 53%, 51%, 0.5)",
  3000000,
  "hsla(197, 74%, 43%, 0.5)",
  11847635,
  "hsla(197, 74%, 43%, 0.5)"
 ]
 const agglomeration_stroke_color =[
  "step",
  ["get", "Population_2015"],
  "hsla(0, 0%, 8%, 1)",
  10000,
  "hsla(55, 30%, 93%, 1)",
  30000,
  "hsla(60, 100%, 90%, 1)",
  100000,
  "hsla(98, 55%, 81%, 1)",
  300000,
  "hsla(166, 44%, 65%, 1)",
  1000000,
  "hsla(186, 53%, 51%, 1)",
  3000000,
  "hsla(197, 74%, 43%, 1)",
  11847635,
  "hsla(197, 74%, 43%, 1)"
 ]

// const options = [];
// country_label.map(v=>{return options.push({label:v, value:v})});
// Values.map(v=>{return options.push({label:agglo_label[v[1]-1]+", "+country_label[v[0]-1], value:v})});
// console.log(Values.map(v=>{return {label:agglo_label[v[1]]+", "+country_label[v[0]], value:v}}))



//// Basic setting of the map
class Application extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lng: 5,
      lat: 0,
      zoom: 2,
      selectedOption: {label: 'Africa', value: ''},
      selectedCountry: "AE",
      name: "Click the country to check SCI",
    };
  }
componentDidMount() {
//// Initial map setting

let selected = [];
let scidata= [];
sci.map(e => e.start===this.state.selectedCountry? selected.push(e) : null)
console.log(selected)
selected.map(e => scidata.push(e.sci))
console.log(scidata)
console.log(quantile(scidata, .20))
//Object.keys(selected).map(e => console.log(e))



     this.map = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mkmd/ckguwdhux0j7719p9rz2hpmpi', /// Select mapstyle from mapbox studio
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom
    });
    // this.map.on('move', () => {
    //   this.setState({
    //   lng: this.map.getCenter().lng.toFixed(4),
    //   lat: this.map.getCenter().lat.toFixed(4),
    //   zoom: this.map.getZoom().toFixed(2)
    //   });
    // });
//// Import Africapolis agglomerations from mapbox
    this.map.on('load', () => {

// Add Country shape
      this.map.addSource('africapolis_country', { type: 'vector', url: 'mapbox://mapbox.country-boundaries-v1'});
// Add Agglomerations
  //    this.map.addSource('africapolis_agglos', { type: 'vector', url: 'mapbox://mkmd.3e0rk98j'});
// Add Country label
     this.map.addSource('africapolis_country_label', { type: 'vector', url: 'mapbox://mapbox.mapbox-streets-v8'});
// Add Region labelled
  //    this.map.addSource('africapolis_region_label', { type: 'vector', url: 'mapbox://mkmd.3yut44ue'});

// Add Country layer
      this.map.addLayer({
            id: 'country',
            source:'africapolis_country',
            type: 'fill',
            'source-layer':'country_boundaries',
            //filter:["==","Region_ID",1],
            paint: {

              'fill-color': '#fdfdf5',
              'fill-opacity': ['case',['boolean', ['feature-state', 'hover'], false],0.2,0]
            }
        });


        this.map.addLayer({
              id: 'country_labels',
              source:'africapolis_country_label',
              type: 'symbol',
              'source-layer':'place_label',
              //filter:[  "match",  ["get", "class"],  "country",  [    "match",    ["get", "worldview"],    ["all", "US"],    true,    false  ],  "disputed_country",  [    "all",    [      "==",      ["get", "disputed"],      "true"    ],    [      "match",      ["get", "worldview"],      ["all", "US"],      true,      false    ]  ],  false],
              layout: {
                'text-field':["get","name_en"],
                'text-font': [   "DIN Pro Medium",  "Arial Unicode MS Regular"],
                //'text-size': ["interpolate", ["linear"], ["zoom"], 2, ["step", ["get", "scalerank"], 10, 3, 8, 5, 5 ], 9, ["step", ["get", "scalerank"], 35, 3, 27, 5, 22 ] ],
              },
              paint:{//'text-opacity':[ "step",["zoom"],  0,  3,  1,  22,  1],
                     'text-color':['case',['boolean', ['feature-state', 'hover'], false],"hsl(0,0%,0%)","hsl(215, 30%, 75%)"],
