const [Dist, setDist] = useState(0)

const TimeSlider = ({ Dist, width }) => {
  return (    
    <div style={{ width: `${width}px` }} className={classes["input-slider"]}>
      <input type="range"
        min={0}
        max={2000}
        value={Dist}
        step={10}
        onChange={(event) =>
           setDist(event.target.value)
          }
      />
      {<span>{Dist}</span>}
    </div>
  );
};


