import React, { useState, useRef } from 'react';
import Select from 'react-select'
import './index.css';
import greendb from './greendb.json';
import wordlist from './wordlist.json';



const App = props => {

const options = []
wordlist.map(e => options.push({value: e.name, label: e.name+"  "+e.freq}))
greendb.map(e => e.keyword=e.Name.split(' '))

const wordAvoid = ["THE","OF","IN","AND","TO","FOR","A","IS","WITH","ON","FROM","-","USING","HOW","BY","NEW","INTO","AN","&","WHAT","ABOUT",2020,"ARE","AT","IT","CHANGE","FIRST",10,"WORLD?????S","USE","OVER","HAS","|","ONE",2019,"TIME",2050,"OR","?????",":"]

const [selectedOption, setSelectedOption] = useState(null);

  
return (
<div>
<Select 
options={options} 
// isMulti 
// defaultValue={selectedOption}
onChange={setSelectedOption}
/>
<button onClick={()=> setSelectedOption("Korea")}>Korea</button>
<button onClick={()=> console.log("KOREA")}>Korea</button>
</div>
  );
};

export default App;
